INSERT INTO `wst_postmeta` VALUES("24279", "1114", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_0", "Once you purchase a policy, you will get a general Certificate of Insurance emailed to you from the insurance carrier, Hiscox. If you need a specific person or organization listed on your certificate, you can create an unlimited number of these for free <a href=\"https://www.contacthiscox.com/\" target=\"_blank\">using this online form</a> (select \"Issue an Additional Insured an Accord Certificate\"). You can also contact us at support@next-insurance.com or call (855) 222-5919.");
