INSERT INTO `wst_postmeta` VALUES("42902", "754", "_crb_block_layouts_text_editor-_crb_content_2", "<div class=\"container-500\">\r\n<h3 class=\"item-title uk-text-center\">All plans include General Liability coverage</h3>\r\n<ul>\r\n 	<li>\r\n<div class=\"list-title\">Bodily injury</div>\r\n<div class=\"list-content\">Someone trips over your tools and breaks their leg</div></li>\r\n 	<li>\r\n<div class=\"list-title\">Property damage</div>\r\n<div class=\"list-content\">You drop heavy duty equipment inside your client\'s house and damage their hardwood floor</div></li>\r\n 	<li>\r\n<div class=\"list-title\">Medical payments</div>\r\n<div class=\"list-content\">Someone gets injured at your work site and asks you to pay for $1,000 in medical expenses instead of suing you</div></li>\r\n 	<li>\r\n<div class=\"list-title\">Advertising harm</div>\r\n<div class=\"list-content\">Someone sues you for improper use of images in your advertising</div></li>\r\n 	<li>\r\n<div class=\"list-title\">Libel, slander and defamation</div>\r\n<div class=\"list-content\">An employee bad-mouths a client on Facebook and the client sues you</div></li>\r\n 	<li>\r\n<div class=\"list-title\">Court and legal fees</div>\r\n<div class=\"list-content\">You need an attorney to defend against a lawsuit even if you did nothing wrong</div></li>\r\n</ul>\r\n</div>\r\n<div class=\"uk-text-center uk-text-small\">This is a brief summary of your policy and does not supersede the policy documents. If you want full details, please call us.</div>\r\n<div class=\"container-500\"></div>");
