INSERT INTO `wst_postmeta` VALUES("42906", "754", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_0", "We give you unlimited certificates of insurance, free of charge. Once you\'ve purchased a policy, <a href=\"https://track.next-insurance.com/links?serial=65551&channel=ps&source=product&campaign=get-coi&adgroup=plumbers&position=lp-faq\" target=\"_blank\">Click Here</a> to input the details of your general contractor, owner, or lessee. We\'ll get you your certificate as soon as your policy is ready, within one business day.");
