INSERT INTO `wst_postmeta` VALUES("45349", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_0", "We give you unlimited certificates of insurance, free of charge. Once you\'ve purchased a policy, please email us or give us a call at 855-222-5919 and we\'ll get you your certificate as soon as your policy is ready, within one business day.");
INSERT INTO `wst_postmeta` VALUES("45350", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_accordion_title_1", "What exactly does my coverage include?");
INSERT INTO `wst_postmeta` VALUES("45351", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_1", "At the highest level, you\'re getting General Liability, Professional Liability, and Accident & Health coverage.  For the details on exactly what this means, please give one of our advisors a call at 855-222-5919.");
INSERT INTO `wst_postmeta` VALUES("45352", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_accordion_title_2", "Do I need an in-home daycare license to get insurance?");
INSERT INTO `wst_postmeta` VALUES("45353", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_2", "No, we don\'t require you to have a license to purchase insurance with us. However, if your state requires one - we\'ll let you know if it does as part of the application - we expect that you\'ll provide your license or certification within 40 days after you purchase.");
INSERT INTO `wst_postmeta` VALUES("45354", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_accordion_title_3", "Do I have to pay the entire annual premium now?");
INSERT INTO `wst_postmeta` VALUES("45355", "1584", "_crb_block_layouts_accordion-_crb_accordion_item_3_-_crb_coverage_content_3", "No, to make it easier on your budget we allow you to make monthly payments.");
